		<?php echo apply_filters( 'universal_end_of_page', '' ); ?>		<div id="footer" class="footer">			<?php 				/*				 *	Aria labelledby adds relationship between the footer and it's heading. 				 */			?>			<footer role="contentinfo" aria-labelledby="footer-header">				<h1 class="screen-reader-text" id="footer-header"><?php _e( 'Footer Content', 'universal' ); ?></h1>				<?php echo apply_filters( 'universal_before_secondary_menu', '' ); ?>				<div class='secondary-menu'>					<?php 						/*						 *	Aria label differentiates between the secondary and primary nav regions.						 */					?>					<nav role="navigation" aria-label='<?php _e( 'Secondary Navigation', 'universal' ); ?>'>					<?php wp_nav_menu( array( 'theme_location'=>'secondary', 'depth'=>1 ) ); ?>					</nav>				</div>				<?php echo apply_filters( 'universal_after_secondary_menu', '' ); ?>				<?php echo apply_filters( 'universal_footer', "<p>&copy; " . get_bloginfo('name') . ", " . date('Y') . "</p>" ); ?>				<?php echo apply_filters( 'universal_after_copyright', '' ); ?>			</footer>		</div>		<?php echo apply_filters( 'universal_after_footer', '' ); ?>	</div>	<?php wp_footer(); ?></body></html>